import { React,useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import { signInWithEmailAndPassword } from "firebase/auth";
import { auth } from "../../../firebase/connection.js";
import arcoIris from "../../Assets/arcoIris.png"
import "./Style.css"


const Login =() => {
  const navigate = useNavigate();
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  /*const [values, setValues] = useState({
    email: "",
    pass: "",
  });*/
  const [errorMsg, setErrorMsg] = useState("");
  const [submitButtonDisabled, setSubmitButtonDisabled] = useState(false);

  const handleSubmission = () => {
    alert("as")
    if (!email.email || !password.password) {
      setErrorMsg("Rellene todos los campos");
      return;
    }
    setErrorMsg("");

    setSubmitButtonDisabled(true);
    signInWithEmailAndPassword(auth, email, password)
      .then(async (res) => {
        setSubmitButtonDisabled(false);

        navigate("/");
      })
      .catch((err) => {
        setSubmitButtonDisabled(false);
        setErrorMsg(err.message);
      });
  };
  return (
    <div>
      <div className="ContainerView">
        <img className="ContainerImage" src={arcoIris} ></img>
        <h1 className="StyleTitle">Lab Notes</h1>

        <div>
        <h3 className="StyleText">Correo</h3>
        <input className="StyleInput"
          label="Email"
          value={email}
          onChange={(event) =>
            setEmail(event.target.value)
          }/>
         <h3 className="StyleText">Contraseña</h3>
        <br/>
        <input
          label="Password"
          value={password}
          onChange={(event) =>
            setPassword(event.target.value)
          }
          placeholder="Enter Password"
        />
        </div>

        <div >
          <b >{errorMsg}</b>
          <button disabled={submitButtonDisabled} onClick={handleSubmission}>
            Login

          </button>
          <p>
            Already have an account?{" "}
            <span>
              <Link to="/register">Sign up</Link>
            </span>
          </p>
        </div>
      </div>
    </div>
  );
}

export default Login;

