 let [state,setState]= useState(0);
const buttonState = () => {
  switch(state){
    case 0 :
      state = 1;
      setState(state);
    break;
    case 1 :
      state = 2;
      setState(state);
    break;
    default:
      state = 0 ; break;
  }
};
 */
