  const userState = collection(db, "ordenes", id);  

  switch(user.state){
    case 0 :
      try{
        await updateDoc(userState, {
          state: 1,
        })
        onClose()
      } catch (err) {
        alert(err)
      }    
    // state = 1;
    //  setState(state);
    insertOrder(user.id,1)
    break;
    case 1 :
      //state = 2;

      //setState(state);
    break;
    default:
    //  state = 0 ;
       break;
  }
};
/**침------------------- */
  return (
    <div>
      <header className="header">
        <img
          src="https://firebasestorage.googleapis.com/v0/b/burger-queen-d0e74.appspot.com/o/logo-bq.PNG?alt=media&token=ff5fc092-8e35-45fc-ab81-6b4a4a7efa45"
          alt="logoBurgerQueen"
          className="logoBurgerQueen"
        />
      </header>
      <ul className="containerNavbarChef">
        <text className="stateOrdersTitle">ESTADO DE PEDIDOS</text>
        <div className="stateOrders">
          <text className="redBall">游댮 Pendiente</text>
          <text className="greenBall">游릭 En proceso</text>
        </div>
      </ul>

      <div className="containerOrders">
      {users.map((user, id) => {
        return (
            <div className={'containerOrderChef '+ (user.state ==0 ? 'containerOrderChef' :user.state ==1 ?'containerOrderChef-preparing': 'containerOrderChef-send')}
            key={id}>
              <div className="numberTable">
                N칰mero de mesa: {user.numberClient}
              </div>

              <div className="listOrderChef">
                {user.productCar.map((product, id) => {
                  return (
                    <div className="productOrderChef" key={id}>
                      <h1 className="quantifyOrder">({product.quantity})</h1>
                      <h1 className="productOrder">{product.product}</h1>
                    </div>
                  );
                })}
              </div>

              <div className="listCommentChef">
                <h1 className="commentsTitleStyle">
                  COMENTARIO:<br></br>
                </h1>
                <div className="commentsTextStyle">{user.comentOrder}</div>
              </div>
              <button className={'buttonStyle '+ (user.state ==0 ? 'buttonStyle':user.state ==1 ?'buttonStyle-preparing': 'buttonStyle-send')}
               onClick={ ()=> buttonState(user)}> 
              {user.state ? 'close':'open'} 
              </button>
            </div> 
        );
      })}
      </div>
    </div>
  );
};

export default Chef;


